import { useState } from "react";
import CTForm from "./CTForm";

const SimpleInput = (props) => {
const [task,setTask]=useState("");



  const formSubmissionHandler = (event) => {
    event.preventDefault();

  };

  const nameHandeler = (event) => {
    // console.log(event.target.value);
    setTask(event.target.value);
  };

  return (
    <form action={formSubmissionHandler}>
      <div className="form-control">
        <div className="control-group">
          <CTForm
            lable="Task Name"
            inputId="task"
            onChangeName={nameHandeler}
            placeholder="enter task name"
          />
          <CTForm lable="Assign"
          placeholder="asign member"
           />
        </div>
        <div className="control-group">
          <CTForm lable="Lable" />

          <CTForm lable="Reporter" />
        </div>

         <div className="control-group">
          <CTForm lable="Flag" />

          <CTForm lable="Link to" />
        </div>
        <div className="control-group">
          <CTForm lable="Start Date" 

         />

          <CTForm lable="End Date" />
        </div>


        <br />
        <div className='text'>
        <textarea></textarea>
        </div>
      </div>

      <div className="form-actions">
        <button type="submit">Submit</button>

      </div>
    </form>
  );
};

export default SimpleInput;

